import React, { useContext, useState,  } from 'react';
import { Link } from 'react-router-dom';
import { useNavigation } from '../contexts/NavigationContext';
import { UserContext } from '../contexts/UserContext';
import Logo from '../assets/logo.png';
import LoginIcon from '../assets/user.png';

const Header = () => {
    const { user, setUser } = useContext(UserContext);
    const [showUsername, setShowUsername] = useState(false);
    const { navigateTo } = useNavigation();

    const toggleUsernameDisplay = () => {
        setShowUsername(!showUsername);
    };

    const handleLogout = () => {
        // Perform logout actions here
        setUser(null);
        // Redirect to login page after logout
        navigateTo('/login'); 
    };

    return (
        <header>
            <div className="header-logo">
                <Link to="/"><img className="Logo-img" src={Logo} alt='Trafiri Logo' /></Link>
            </div>
            <nav className='navigation'>
                <ul className='list'>
                    <li><Link to="/">Home</Link></li>
                    <li><Link to="/activities">Activities</Link></li>
                    <li><Link to="/places">Places</Link></li>
                    <li><Link to="/review">Review</Link></li>
                    {!user && <li><Link to="/login">Login</Link></li>}
                    {!user && <li><Link to="/register">Register</Link></li>}
                    <li><Link to="/contact">Contact</Link></li>
                </ul>

                <div className="nav-login" onClick={toggleUsernameDisplay}>
                    <div
                        className="login-image"
                        
                    >
                        <img src={LoginIcon} alt="Login" />
                    </div>
                    {showUsername && (
                        <div className="user-details">
                            <span className="username">{user ? user.firstName : 'Guest'}</span>
                            {user && <button className='user-change' onClick={handleLogout}>Change User</button>}
                        </div>
                    )}
                </div>
            </nav>
        </header>
    );
};

export default Header;
