import React, { useContext, useState, useEffect } from 'react';
import { Link } from 'react-router-dom'; // Import useHistory from react-router-dom
import { useNavigation } from './NavigationContext';
import { UserContext } from '../contexts/UserContext'; // Adjust the path accordingly
import Logo from '../assets/logo.png'; // Import Logo
import LoginIcon from '../assets/user.png'; // Import Login Icon (use an actual image path)

const Header = () => {
    const { user, setUser } = useContext(UserContext);
    const [imageColor, setImageColor] = useState('#000'); // default color
    const [showUsername, setShowUsername] = useState(false);

    useEffect(() => {
        const randomColor = `#${Math.floor(Math.random() * 16777215).toString(16)}`;
        setImageColor(randomColor);
    }, []);

    const toggleUsernameDisplay = () => {
        setShowUsername(!showUsername);
    };

    const handleLogout = () => {
        // Perform logout actions here
        setUser(null); // Example: clear user context/state
        history.push('/login'); // Redirect to login page after logout
    };

    return (
        <header>
            <div className="header-logo">
                <Link to="/"><img className="Logo-img" src={Logo} alt='Trafiri Logo' /></Link>
            </div>
            <nav className='navigation'>
                <ul className='list'>
                    <li><Link to="/">Home</Link></li>
                    <li><Link to="/activities">Activities</Link></li>
                    <li><Link to="/places">Places</Link></li>
                    <li><Link to="/review">Review</Link></li>
                    {!user && <li><Link to="/login">Login</Link></li>}
                    {!user && <li><Link to="/register">Register</Link></li>}
                    <li><Link to="/contact">Contact</Link></li>
                </ul>

                <div className="nav-login" onClick={toggleUsernameDisplay}>
                    <div
                        className="login-image"
                        style={{ backgroundColor: imageColor }}
                    >
                        <img style={{ backgroundColor: imageColor }} src={LoginIcon} alt="Login" />
                    </div>
                    {showUsername && (
                        <div className="user-details">
                            <span className="username">{user ? user.firstName : 'Guest'}</span>
                            {user && <button onClick={handleLogout}>Logout</button>}
                        </div>
                    )}
                </div>
            </nav>
        </header>
    );
};

export default Header;
