import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom'; // Import Link for client-side navigation
import Logo from '../assets/logo.png'; // Import Logo
import UserIcon from '../assets/user.png'; // Import User Icon

const Header = () => {
    const [user, setUser] = useState({ firstName: 'Guest' });
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [imageColor, setImageColor] = useState('#000'); // default color

    useEffect(() => {
        // Check if user is logged in
        const storedUser = JSON.parse(localStorage.getItem('user'));
        // If user is logged in, set user details
        if (storedUser) {
            setUser(storedUser);
            setIsLoggedIn(true);
            const randomColor = `#${Math.floor(Math.random() * 16777215).toString(16)}`;
            setImageColor(randomColor);
        }
    }, []);

    const handleLogin = () => {
        // Redirect to login page
        window.location.href = '/login';
    };

    return (
        <header>
            <div className="header-logo">
                <Link to="/"><img className="Logo-img" src={Logo} alt='Trafiri Logo' /></Link>
            </div>
            <nav className='navigation'>
                <ul className='list'>
                    <li><Link to="/">Home</Link></li>
                    <li><Link to="/activities">Activities</Link></li>
                    <li><Link to="/places">Places</Link></li>
                    <li><Link to="/review">Review</Link></li>
                    <li><Link to="/contact">Contact</Link></li>
                    {!isLoggedIn && <li><Link to="/login">Login</Link></li>}
                    {!isLoggedIn && <li><Link to="/register">Register</Link></li>}
                </ul>
                <div className="nav-login" onClick={handleLogin} style={{ color: imageColor }}>
                    <img src={UserIcon} alt="Login" className="login-image" />
                    <span>{user.firstName}</span>
                </div>
            </nav>
        </header>
    );
}

export default Header;
